local self = {}GCompute.NamespaceBuilder = GCompute.MakeConstructor (self, GCompute.ASTVisitor)function self:ctor (compilationUnit)	self.CompilationUnit = compilationUnit	self.AST = self.CompilationUnit:GetAbstractSyntaxTree ()endfunction self:VisitRoot (blockStatement)	blockStatement:SetNamespace (blockStatement:GetNamespace () or GCompute.NamespaceDefinition ())	blockStatement:GetNamespace ():SetConstructorAST (blockStatement)endfunction self:VisitBlock (blockStatement)	blockStatement:SetNamespace (blockStatement:GetNamespace () or GCompute.NamespaceDefinition ())	blockStatement:GetNamespace ():SetContainingNamespace (blockStatement:GetParentNamespace ())	blockStatement:GetNamespace ():SetConstructorAST (blockStatement)endfunction self:VisitStatement (statement)	if statement:HasNamespace () then		statement:SetNamespace (statement:GetNamespace () or GCompute.NamespaceDefinition ())		statement:GetNamespace ():SetContainingNamespace (statement:GetParentNamespace ())	end		if statement:Is ("FunctionDeclaration") then		statement:GetParentNamespace ():AddFunction (statement:GetName (), statement:GetParameterList ())	elseif statement:Is ("VariableDeclaration") then		statement:GetParentNamespace ():AddMemberVariable (statement:GetName (), statement:GetType ())	endend