local self = {}GCompute.MergedNamespaceDefinition = GCompute.MakeConstructor (self)--- @param name The name of this namespacefunction self:ctor (name)	self.Name = name		self.SourceNamespaces = {}		self.Members = {}	self.Metadata = {}end--- Adds a source namespace from which members will be obtained-- @param namespaceDefinition Source namespace definition from which members will be obtainedfunction self:AddSourceNamespace (namespaceDefinition)	self.SourceNamespaces [#self.SourceNamespaces + 1] = namespaceDefinitionend