local self = {}VFS.IFileStream = VFS.MakeConstructor (self)function self:ctor ()	self.AuthId = ""	self.Closed = false	self.Position = 0endfunction self:Close ()	self.Closed = trueendfunction self:Flush ()endfunction self:GetFile ()endfunction self:GetLength ()	return self:GetFile ():GetSize ()endfunction self:GetDisplayPath ()	return self:GetFile ():GetDisplayPath ()endfunction self:GetPath ()	return self:GetFile ():GetPath ()endfunction self:GetPos ()	return self.Positionendfunction self:IsClosed ()	return self.Closedend--[[	IFileStream:Read (authId, function (returnCode, data))]]function self:Read (size, callback)	callback (VFS.ReturnCode.None, "")endfunction self:Seek (pos)	self.Position = posend