local self = {}VFS.IFileStream = VFS.MakeConstructor (self)function self:ctor ()	self.AuthId = ""	self.Closed = false	self.Position = 0endfunction self:CanWrite ()	return falseendfunction self:Close ()	self:Flush ()	self.Closed = trueendfunction self:Flush ()endfunction self:GetFile ()endfunction self:GetLength ()	return self:GetFile ():GetSize ()endfunction self:GetDisplayPath ()	return self:GetFile ():GetDisplayPath ()endfunction self:GetPath ()	return self:GetFile ():GetPath ()endfunction self:GetPos ()	return self.Positionendfunction self:IsClosed ()	return self.Closedend--[[	IFileStream:Read (size, function (returnCode, data))]]function self:Read (size, callback)	callback (VFS.ReturnCode.Success, "")endfunction self:Seek (pos)	self.Position = posend--[[	IFileStream:Write (size, data, function (returnCode))]]function self:Write (size, data, callback)	callback (VFS.ReturnCode.Success)end